mapper.select:
select count(*) from blog_log  where 1=1 
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and REMARK = #{parm.remark}
	   	
		
	     	and UPDATE_CONTENT = #{parm.updateContent}
	   	
		
	     	and USER_ID = #{parm.userId}
	   	
		
	     	and BLOG_ID = #{parm.blogId}

select * from blog_log where 1=1 
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and REMARK = #{parm.remark}
	   	
		
	     	and UPDATE_CONTENT = #{parm.updateContent}
	   	
		
	     	and USER_ID = #{parm.userId}
	   	
		
	     	and BLOG_ID = #{parm.blogId}
	   	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select b.TITLE as blogTitle, bl.ID as id, DATE_FORMAT(bl.CREATE_TIME,'%Y-%m-%d %H:%s:%i') as createTime, bl.REMARK as remark, u.USERNAME as username from blog_log bl LEFT JOIN blog b on b.ID=bl.BLOG_ID LEFT JOIN `user` u on u.ID = bl.USER_ID where 1=1
and b.TITLE = #{parm.blogTitle}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}
