mapper.select:
select count(*) from blog  where 1=1 
		
	     	and IMG = #{parm.img}
	   	
		
	     	and UPDATE_TIME = #{parm.updateTime}
	   	
		
	     	and TITLE = #{parm.title}
	   	
		
	     	and USER_ID = #{parm.userId}
	   	
		
	     	and CLICK = #{parm.click}
	   	
		
	     	and CONTENT = #{parm.content}
	   	
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and PRE_CONTENT = #{parm.preContent}
	   	
		
	     	and MENU_ID = #{parm.menuId}
	   	
		
	     	and TAG = #{parm.tag}
	   	
		
	     	and STATUS = #{parm.status}

select * from blog where 1=1 
		
	     	and IMG = #{parm.img}
	   	
		
	     	and UPDATE_TIME = #{parm.updateTime}
	   	
		
	     	and TITLE = #{parm.title}
	   	
		
	     	and USER_ID = #{parm.userId}
	   	
		
	     	and CLICK = #{parm.click}
	   	
		
	     	and CONTENT = #{parm.content}
	   	
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and PRE_CONTENT = #{parm.preContent}
	   	
		
	     	and MENU_ID = #{parm.menuId}
	   	
		
	     	and TAG = #{parm.tag}
	   	
		
	     	and STATUS = #{parm.status}
	   	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select b.id as id, b.MENU_ID menuId, b.TITLE as title, DATE_FORMAT(b.CREATE_TIME,"%Y-%m-%d %H:%s:%i") as createTime, DATE_FORMAT(b.UPDATE_TIME,"%Y-%m-%d %H:%s:%i") as updateTime, b.CLICK as click, u.USERNAME as username, bm.`NAME` as menuName from blog b LEFT JOIN `user` u on b.USER_ID = u.ID LEFT JOIN blog_menu bm on b.MENU_ID = bm.ID where 1=1
and b.title like "%"#{parm.title}"%"
and u.USERNAME like "%"#{parm.username}"%"
and b.menu_id = #{parm.menuId}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}
