mapper.select:
select count(*) from collection  where 1=1 
		
	     	and DISCOVERY_ID = #{parm.discoveryId}
	   	
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and USER_ID = #{parm.userId}
	   	
	   	
	     	and BLOG_ID = #{parm.blogId}

select * from collection where 1=1 
		
	     	and DISCOVERY_ID = #{parm.discoveryId}
	   	
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and USER_ID = #{parm.userId}
	   	
	   	
	     	and BLOG_ID = #{parm.blogId}
	   	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select count(*) FROM collection c LEFT JOIN discovery d on d.ID = c.DISCOVERY_ID LEFT JOIN `user` u1 on u1.ID=d.USER_ID LEFT JOIN `user` u2 on u2.ID=c.USER_ID where 1=1
and d.title=#{parm.title}
and u2.username=#{parm.username}

select u1.USERNAME as author, u2.USERNAME as collectionUser, c.ID as id, d.TITLE as title, DATE_FORMAT(c.CREATE_TIME,'%Y-%m-%d %H:%s:%i') as createTimeStr FROM collection c LEFT JOIN discovery d on d.ID = c.DISCOVERY_ID LEFT JOIN `user` u1 on u1.ID=d.USER_ID LEFT JOIN `user` u2 on u2.ID=c.USER_ID where 1=1
and d.title=#{parm.title}
and u2.username=#{parm.username}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}

	SELECT 
	d.TITLE as title,
	d.ID as discoveryId,
	u.USERNAME as postName,
	DATE_FORMAT(c.CREATE_TIME,'%Y-%m-%d %H:%i:%s') as createTimeStr,
	d.CONTENT as content,
	d.IMG_PATH as imgPath
	FROM collection c 
	LEFT JOIN discovery d on c.DISCOVERY_ID=d.ID
	LEFT JOIN `user` u on c.USER_ID=u.ID
	where 1=1 
	
		and c.user_id=#{parm.userId}
	
	
   		and c.id <#{parm.id}
   	
   	
   		and c.id >#{parm.id}
   	
	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.limit}
