mapper.select:
select count(*) from comment  where 1=1 
		
	     	and DISCOVERY_ID = #{parm.discoveryId}
	   	
		
	     	and TO_ID = #{parm.toId}
	   	
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and USER_IP = #{parm.userIp}
	   	
		
	     	and BLOG_ID = #{parm.blogId}
	   	
		
	     	and FROM_ID = #{parm.fromId}
	   	
		
	     	and MESSAGE_CONTENT = #{parm.messageContent}

select * from comment where 1=1 
		
	     	and DISCOVERY_ID = #{parm.discoveryId}
	   	
		
	     	and TO_ID = #{parm.toId}
	   	
		
	     	and CREATE_TIME = #{parm.createTime}
	   	
		
	     	and USER_IP = #{parm.userIp}
	   	
		
	     	and BLOG_ID = #{parm.blogId}
	   	
		
	     	and FROM_ID = #{parm.fromId}
	   	
		
	     	and MESSAGE_CONTENT = #{parm.messageContent}
	   	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select * from comment where 1=1
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select c.ID as commentId, c.MESSAGE_CONTENT as messageContent, DATE_FORMAT(c.CREATE_TIME,'%Y-%m-%d %H:%m:%i') as createTimeStr, uf.ID as fromId, uf.USERNAME as fromName, uf.AVATAR as fromAvatar, ut.ID as toId, ut.USERNAME as toName, ut.AVATAR as toAvatar from `comment` c LEFT JOIN `user` uf on uf.ID = c.FROM_ID LEFT JOIN `user` ut on ut.ID = c.TO_ID where 1=1
and c.DISCOVERY_ID = #{parm.discoveryId}
and c.TO_ID = #{parm.toId}
and c.BLOG_ID = #{parm.blogId}
and c.FROM_ID = #{parm.fromId}
and c.id < #{parm.id} order by c.id asc
and c.id > #{parm.id} order by c.id desc
order by c.id asc
limit #{parm.pageSize}
