mapper.select:
	SELECT count(*) FROM picblog_log  
	WHERE 1=1 

     	and PICBLOG_ID = #{parm.picBlogId}


     	and USER_ID = #{parm.userId}

	SELECT bl.ID as id, bl.PICBLOG_ID as picBlogId, bl.CREATE_TIME as createTime, 
		bl.REMARK as remark,bl.USER_ID as userId, u.USERNAME as username
	FROM picblog_log bl 
	LEFT JOIN `user` u on u.ID = bl.USER_ID
	WHERE 1=1 

     	and PICBLOG_ID = #{parm.picBlogId}


     	and USER_ID = #{parm.userId}

	ORDER BY ID DESC 
	LIMIT #{page.pageNum}, #{page.pageSize}

	SELECT *
	FROM picblog_log bl 
	WHERE 1=1 

     	and PICBLOG_ID = #{parm.picBlogId}


     	and USER_ID = #{parm.userId}

	ORDER BY ID DESC 
	LIMIT #{page.pageNum}, #{page.pageSize}
