mapper.select:
	SELECT count(*) FROM picblog pb 
	WHERE 1=1 

     	and pb.SHOW_PIC <> ''


     	and pb.STATUS = #{parm.status}


     	and pb.USER_ID = #{parm.userId}


     	and pb.TITLE like '%${parm.title}%'

	SELECT pb.TITLE as title, pb.ID as id, pb.STATUS as status, pb.USER_ID as userId, pb.CREATE_TIME as createTime, 
	pb.UPDATE_TIME as updateTime, pb.CLICK as click, pb.SHOW_PIC as showPic, pb.DESCRIPTION as description
	FROM picblog pb 
	WHERE 1=1 

     	and pb.SHOW_PIC <> ''


     	and pb.STATUS = #{parm.status}


     	and pb.USER_ID = #{parm.userId}


     	and pb.TITLE like '%${parm.title}%'

	ORDER BY pb.ID DESC 
	LIMIT #{page.pageNum}, #{page.pageSize}

	SELECT * FROM picblog pb 
	WHERE 1=1 

     	and pb.SHOW_PIC <> ''


     	and pb.STATUS = #{parm.status}


     	and pb.USER_ID = #{parm.userId}


     	and pb.TITLE like '%${parm.title}%'

	ORDER BY pb.ID DESC

		LIMIT #{page.pageNum}, #{page.pageSize}
