mapper.select:
select count(*) from sys_cfg  where 1=1 

	     	and REMARK = #{parm.remark}


	     	and COUNT_JSON = #{parm.countJson}

select * from sys_cfg where 1=1 

	     	and REMARK = #{parm.remark}


	     	and COUNT_JSON = #{parm.countJson}


   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}

select * from sys_cfg where 1=1

   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}
