mapper.select:
select count(*) from user_role  where 1=1 

	     	and ROLE_NAME = #{parm.roleName}


	     	and ROLE_DESCRIPTION = #{parm.roleDescription}

select * from user_role where 1=1 

	     	and ROLE_NAME = #{parm.roleName}


	     	and ROLE_DESCRIPTION = #{parm.roleDescription}


   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}

